--
-- Structură tabel pentru tabel `chat_messages`
--

CREATE TABLE `chat_messages` (
  `id` bigint UNSIGNED NOT NULL,
  `user_id` bigint UNSIGNED NOT NULL,
  `user_name` varchar(120) COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `role` enum('USER','ADMIN') COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'USER',
  `body` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `client_id` varchar(64) COLLATE utf8mb4_unicode_520_ci DEFAULT NULL,
  `reply_to` int DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_520_ci;

